package com.DailyCheck;

import java.util.*;

public class Day6_No904_totalFruit {
    public static int totalFruit(int[] fruits) {
        int result = 0;
        List<Integer> fruitIndexArray = new ArrayList<>();
        fruitIndexArray.add(0);
        for(int i = 1; i < fruits.length; i ++ ){
            if(fruits[i] != fruits[i-1]) {
                fruitIndexArray.add(i);
            }
        }

        int leftIndex = 0;
        int rightIndex = 0;
        int leftValue = fruits[fruitIndexArray.get(0)];
        int rightValue = fruits[fruitIndexArray.get(0)];
        for(int i = 1; i < fruitIndexArray.size(); i ++ ) {
            int tempV = fruits[fruitIndexArray.get(i)];
            if(leftValue != tempV && rightValue != tempV) {
                result = Math.max(result, fruitIndexArray.get(i) - leftIndex);
                leftValue = fruits[rightIndex];
                rightValue = tempV;
                leftIndex = rightIndex;
                rightIndex = fruitIndexArray.get(i);
            } else {
                rightIndex = fruitIndexArray.get(i);
            }
        }

        return Math.max(result, fruits.length - leftIndex );
    }

    public static void main(String[] args) {
//        int[] fruits = {3,3,3,1,2,1,1,2,3,3,4};
//        System.out.println(totalFruit(fruits));
        String testString = "";
        byte[] byteString = testString.getBytes();
        System.out.println(byteString.length);
    }
}
